mapper.select:
select e.eno, m.mno, e.title, e.cont, e.date, e.loc, e.addr, e.viewcnt, e.pub, e.createdate, e.thumbnail
from event e
join member m on e.mno = m.mno where e.pub = false order by eno desc LIMIT #{pageStart}, #{perPageNum}

select e.eno, m.mno, e.title, e.cont, e.date, e.loc, e.addr, e.viewcnt, e.pub, e.createdate, e.thumbnail
from event e
join member m on e.mno = m.mno where m.mno =#{memberNo} order by eno desc LIMIT #{cri.pageStart}, #{cri.perPageNum}

select e.eno, m.mno, m.nick, e.title, e.cont, e.date, e.loc, e.addr, e.viewcnt, e.pub, e.createdate, e.thumbnail
from event e
join member m on e.mno = m.mno where e.pub = false order by eno desc LIMIT #{pageStart}, #{perPageNum}

mapper.update:
update event set
title = #{title},
cont = #{content}
where
eno = #{no}
